import React, { useState } from "react";

const Form = ({ appName, data, onSubmit, onClose }) => {
  const [formState, setFormState] = useState(data);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormState({ ...formState, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(formState);
  };

  return (
    <div className="form-container">
      <h2>Configuration for {appName}</h2>

      <button className="close-button" onClick={onClose}>
        Close
      </button>

      <form onSubmit={handleSubmit}>
        {/* Replace with your actual form fields and inputs */}
        <label htmlFor="config1">Configuration 1:</label>
        <input type="text" id="config1" name="config1" value={formState.config1} onChange={handleChange} />

        {/* Add more fields as needed */}

        <button type="submit">Save configuration</button>
      </form>
    </div>
  );
};

export default Form;
